    border: 1px solid lightgray;
    padding: 20px;
    background-color: rgb(106, 108, 109);
    margin-left: 314px;
    margin-top: 60px;
}
.login__signInButton {
    background: #f0c14b;
    border-radius: 2px;
    width: 100%;
    height: 30px;
    border: 1px solid;
    margin-top: 10px;
    border-color: #a88734 #9c7e31 #846a29;
  }

  .login__registerButton{
    border-radius: 2px;
    width: 100%;
    height: 30px;
    border: 1px solid;
    margin-top: 10px;
    border-color: darkgray;
    background:white;
    display: flex;
    align-items: center;
    padding-left:45px;
    
  }


